import "./Investment.css";

const Investment = (props) => {
  console.log(props.investments);

  const investmentsContent = props.investments.map((investment) => {
    const totalSavings = investment.savingsEndOfYear;
    const year = investment.year;

    return (
      <tr>
        <td>{year}</td>
        <td>{totalSavings}</td>
        <td>{investment.yearlyInterest}</td>
        <td>TOTAL INTEREST GAINED</td>
        <td></td>
      </tr>
    );
  });

  return (
    <table className="result">
      <thead>
        <tr>
          <th>Year</th>
          <th>Total Savings</th>
          <th>Interest (Year)</th>
          <th>Total Interest</th>
          <th>Invested Capital</th>
        </tr>
      </thead>
      <tbody>{investmentsContent}</tbody>
    </table>
  );
};

export default Investment;
