import "./Investment.css";

const Investment = (props) => {
  const formatter = new Intl.NumberFormat('')

  const investmentsContent = props.investments.map((investment) => {
    const totalSavings = Math.round(investment.savingsEndOfYear * 1000) / 1000;
    const year = investment.year;
    const interestYearly = Math.round(investment.yearlyInterest * 1000) / 1000;
    const totalInterest = investment.yearlyContribution;
    const id = Math.random();

    return (
      <tr key={id}>
        <td>{year}</td>
        <td>{totalSavings}</td>
        <td>{interestYearly}</td>
        <td>
          {Math.round(
            (totalSavings - props.initialInvestment - totalInterest) *
              year *
              1000
          ) / 1000}
        </td>
        <td>{props.initialInvestment + totalInterest * year}</td>
      </tr>
    );
  });

  return (
    <table className="result">
      <thead>
        <tr>
          <th>Year</th>
          <th>Total Savings</th>
          <th>Interest (Year)</th>
          <th>Total Interest</th>
          <th>Invested Capital</th>
        </tr>
      </thead>
      <tbody>{investmentsContent}</tbody>
    </table>
  );
};

export default Investment;
